import React from "react";
import Vmarine from "../img/SmartyPill.jpg";
import SmartTrash from "../img/smarttrash.jpg"
import aguaDeLuz from "../img/agueDeLuz.png";
import Spotr from '../img/IMG_0441.jpg'
import todo from "../img/todolist.png";
import PropTypes from "prop-types";
import Carousel from 'react-images';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

const images=[{source:'../img/SmartyPill.jpg', }]

export default class Projects extends React.Component {

	render() {
		return (
			<React.Fragment>
				<div id="my-projects" className="container-fluid bg-light mt-5">
					<div className=" project-container container bg-light pb-5">
						<h1
							id="projects"
							className={"text-center " + this.props.fadeInRight}>
							Projects
						</h1>
						<Card className={classes.card}>
					      <CardActionArea>
					        <CardMedia
					          className={classes.media}
					          image={SmartTrash}
					          title="Contemplative Reptile"
					        />
					        <CardContent>
					          <Typography gutterBottom variant="h5" component="h2">
					            Lizard
					          </Typography>
					          <Typography variant="body2" color="textSecondary" component="p">
					            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
					            across all continents except Antarctica
					          </Typography>
					        </CardContent>
					      </CardActionArea>
					      <CardActions>
					        <Button size="small" color="primary">
					          Share
					        </Button>
					        <Button size="small" color="primary">
					          Learn More
					        </Button>
					      </CardActions>
					    </Card>
						<div className="row my-5">
							<div
								className={
									"testbg col-12 col-sm-12 col-md-4 " +
									this.props.fadeIn
								}>
								<div className="overlay">
									<div className="text">
										<h2>Smarty Pill</h2>
										<h6> September-December 2019 </h6>
									</div>
									<div>
										<button
											id="smartypill-button"
											className="project-button"
											onClick={() => {
												var modalBG = document.getElementById(
													"gallery-card"
												);
												var marineModal = document.getElementById(
													"smartypill"
												);
												modalBG.style.display = "block";
												marineModal.style.display =
													"block"; 
											}}>
											Learn More
										</button>
									</div>
								</div>
							</div>
							<div
								className={
									"aguabg col-12 col-sm-12 col-md-4 " +
									this.props.fadeIn
								}>

								<div className="overlay">
									<div className="text">
										<h2>A+tandance</h2>
										<h6> January-April 2019 </h6>
									</div>
									<div>
										<button
											id="attendance-button"
											className="project-button"
											onClick={() => {
												var modalBG = document.getElementById(
													"gallery-card"
												);
												var aguaModal = document.getElementById(
													"attendance"
												);
												modalBG.style.display = "block";
												aguaModal.style.display =
													"block";
											}}>
											Learn More
										</button>
									</div>
								</div>
							</div>
							<div
								className={
									"todobg col-12 col-sm-12 col-md-4 " +
									this.props.fadeIn
								}>
